contract LevelConfig {

    /// @dev 玩家升级的数据结构体
    struct LevelCfg {
        uint256 level; // 对应的等级
        uint256[] prizes; // 奖励的物品
        uint256 gold; // 奖励的金币
        uint256 levelup_exp; // 升级所需要的经验值
        uint256 total_exp; // 累计总经验值
        uint8 hunting_num; // 对应的狩猎次数
        uint8 garden_plant_num; // 花园的植物数量
